#include<cstdio>#include<iostream>#include<algorithm>#include<cstring>#include<cstdlib>using namespace std;int f[105][105];string s;int judge(int x1,int y1,int x2,int y2){	int len1=y1-x1+1;	int len2=y2-x2+1;	if(len2%len1) return 0;	for(int i=x2;i<=y2;i++)		if(s[i]!=s[i-len1]) return 0;	return 1;}int work(int x){	int ans=0;	while(x)		x/=10,ans++;	return ans;}int main(){	cin>>s;	int n=s.length();	memset(f,0x7f,sizeof(f));	for(int i=0;i<n;i++)		for(int j=i;j<n;j++)			f[i][j]=j-i+1;	for(int len=1;len<n;len++)		for(int i=0;i<n-len;i++)		{			int j=i+len;			for(int k=i;k<j;k++)			{				if(judge(i,k,k+1,j)==0)					f[i][j]=min(f[i][j],f[i][k]+f[k+1][j]);				else					f[i][j]=min(f[i][j],f[i][k]+work((len+1)/(k-i+1))+2);			}		}	printf("%d",f[0][n-1]);}
